import java.util.*;
class Employee{
  String name;
  int salary;
  Employee(String n,int s){ name=n; salary=s; }
  int netSalary(){ return salary; }
}
class HR extends Employee{
  int bonus;
  HR(String n,int s,int b){ super(n,s); bonus=b; }
  int netSalary(){ return salary+bonus; }
}
class Accountant extends Employee{
  int ded;
  Accountant(String n,int s,int d){ super(n,s); ded=d; }
  int netSalary(){ return salary-ded; }
}
public class Main{
  public static void main(String[] args){
    Scanner sc=new Scanner(System.in);
    String role=sc.next();
    if(role.equals("HR")){
      String n=sc.next(); int s=sc.nextInt(); int b=sc.nextInt();
      System.out.println(new HR(n,s,b).netSalary());
    }else{
      String n=sc.next(); int s=sc.nextInt(); int d=sc.nextInt();
      System.out.println(new Accountant(n,s,d).netSalary());
    }
  }
}

