import java.util.*;
class Animal{ String sound(){ return "Animal"; } }
class Dog extends Animal{ String sound(){ return "Bark"; } }
class Cat extends Animal{ String sound(){ return "Meow"; } }
public class Main{
  public static void main(String[] args){
    Scanner sc=new Scanner(System.in);
    String t=sc.next();
    Animal a;
    if(t.equals("dog")) a=new Dog();
    else a=new Cat();
    System.out.println(a.sound());
  }
}

