import java.util.*;
class Shape{ double area(){ return 0; } }
class Circle extends Shape{ double r; Circle(double x){ r=x; } double area(){ return 3.14*r*r; } }
class Rectangle extends Shape{ double w,h; Rectangle(double a,double b){ w=a; h=b; } double area(){ return w*h; } }
public class Main{
  public static void main(String[] args){
    Scanner sc=new Scanner(System.in);
    String t=sc.next();
    Shape s;
    if(t.equals("circle")) s=new Circle(sc.nextDouble());
    else s=new Rectangle(sc.nextDouble(),sc.nextDouble());
    System.out.println(s.area());
  }
}

import java.util.*;
class Payment{ int amt; Payment(int a){ amt=a; } }
class CashPayment extends Payment{
  CashPayment(int x){ super(x); }
  String pay(){ return "Cash-"+amt; }
}
class CardPayment extends Payment{
  CardPayment(int x){ super(x); }
  String pay(){ return "Card-"+amt; }
}
public class Main{
  public static void main(String[] args){
    Scanner sc=new Scanner(System.in);
    String type=sc.next();
    int a=sc.nextInt();
    if(type.equals("cash")) System.out.println(new CashPayment(a).pay());
    else System.out.println(new CardPayment(a).pay());
  }
}

