import java.util.*;
class A{ protected int val; A(int v){ val=v; } }
class B extends A{ B(int v){ super(v); }
  int add(int n){ return val+n; }
}
public class Main{
  public static void main(String[] args){
    Scanner sc=new Scanner(System.in);
    int v=sc.nextInt();
    int n=sc.nextInt();
    System.out.println(new B(v).add(n));
  }
}

import java.util.*;
class Vehicle{ String drive(){ return "Vehicle"; } }
class Bike extends Vehicle{ String drive(){ return "Bike"; } }
class Car extends Vehicle{ String drive(){ return "Car"; } }
public class Main{
  public static void main(String[] args){
    Scanner sc=new Scanner(System.in);
    String t=sc.next();
    Vehicle v;
    if(t.equals("bike")) v=new Bike();
    else v=new Car();
    System.out.println(v.drive());
  }
}
