def main():
    s = input().strip()
    first = s.find('f')
    if first == -1:
        return
    last = s.rfind('f')
    if first == last:
        print(first)
    else:
        print(first, last)

if __name__ == "__main__":
    main()

def main():
    s = input().strip()
    first = s.find('f')
    if first == -1:
        print(-2)
        return
    second = s.find('f', first+1)
    if second == -1:
        print(-1)
    else:
        print(second)

if __name__ == "__main__":
